import { useState } from "react";
import "./Aplication.css";
import AplicationForm from "./AplicationForm";
import Axios from "axios";
function Aplication() {
  const [userData, setUserData] = useState([]);
  const link ="https://bootcamp-2022.devtest.ge/api/applications?token=ed8a6f5d-f018-4207-a3f4-27a1a340eb18";
  const [arrow, setarrow] = useState(false)
  function personalInfo() {
    Axios.get(link).then((response) => {
      const data = response.data;
          setUserData({
              fname:data.first_name
          })
    });
  }
  console.log(userData[0])
function up() {
    setarrow(!arrow)
    personalInfo()
  }

  return (
    <div>
      <div className="content">
        <h1 className="aplication_h1">Submitted Applications</h1>
        <div className="heading">
          <p className="numbers">1</p>
          <button className="img_butt" onClick={up}>
            {arrow && <i className="arrow down"></i>}
            {!arrow && <i className="arrow up"></i>}
          </button>
        </div>
       {arrow && <AplicationForm  users ={userData[0]}/>}
      </div>
    </div>
  );
}
export default Aplication;
