import { useState } from "react";
import "./Aplication.css";
import AplicationForm from "./AplicationForm";
import Axios from "axios";
function Aplication() {
  const [userData, setUserData] = useState([]);
  const link =
    "https://bootcamp-2022.devtest.ge/api/applications?token=ed8a6f5d-f018-4207-a3f4-27a1a340eb18";
  const [arrow, setarrow] = useState(false);
  function personalInfo() {
    Axios.get(link).then((response) => {
      const data = response.data;
      console.log(data);

      for (let i = 0; i < data.length; i++) {
        setUserData({
          fname: data[i].first_name,
          lname: data[i].last_name,
          email:data[i].email, 
          number:data[i].phone, 
          skill:data[i].skills,
          work_preference:data[i].work_preference,
          had_covid: data[i].had_covid,
          had_covid_at: data[i].had_covid_at,
          vaccinated: data[i].vaccinated,
          vaccinated_at:data[i].vaccinated,
          will_organize_devtalk: data[i].will_organize_devtalk,
          devtalk_topic:data[i].devtalk_topic,
          something_special:data[i].something_special
        });
      }
    });
  }
  function up() {
    setarrow(!arrow);
    personalInfo();
  }


  return (
    <div>
      <div className="content">
        <h1 className="aplication_h1">Submitted Applications</h1>
        <div className="heading">

          <p className="numbers">1</p>
          <button className="img_butt" onClick={up}>
            {arrow && <i className="arrow down"></i>}
            {!arrow && <i className="arrow up"></i>}
          </button>
        </div>
        {arrow && <AplicationForm/>}


      </div>
    </div>
  );
}
export default Aplication;
