import {React, useState} from "react";
import styles from "./PersonalInfo.module.css";

function PersonalInfo() {
  var regex = /[$-/:-?{-~!"^_`\[\]]/ ;
  var mailformat = /^w+([.-]?w+)*@w+([.-]?w+)*(.w{2,3})+$/
  const [fname, setFName] = useState('');
  const [lname, setLName] = useState('');
  const [mail, setmail] = useState('');
  const [nameError,setNameError] = useState('');
  const [lnameError,setLnameError] = useState('');
  const [EmailError,setMailError] = useState('');

  function fistname(event){
    setFName(event.target.value)
  }
  function lastname(event){
    setLName(event.target.value)
  }

  function e_mail(event){
    setmail(event.target.value)
  }
  function submit(){
    if(fname.trim().length < 2 || regex.test(fname)){
      setNameError("Please Enter Valid Name")
    }
    if(lname.trim().length < 2 || regex.test(lname)){
      setLnameError("Please Enter Valid Last Name")
    }
    if(!mail.match(mailformat)){
      EmailError("Please Enter Valid Email")
    }
    if(!mail.match(mailformat) || mail.trim.length !== 0 ){
      EmailError("Please Enter Valid Email")
    }

  }

  console.log(fname , lname)
  return (
    <div className={styles.information}>
      <div className={styles.cont1}>
        <h1>Hey, Rocketeer, what are your coordinates?</h1>
        <div>
          <form className={styles["infoform"]}>
            <input type={"text"} id='name' placeholder="Fist Name" className={styles["infoinput"]} value = {fname} onChange = {fistname}/>
            <p>{nameError}</p>
            <input type={"text"} placeholder="Last Name" className={styles["infoinput"]} value={lname} onChange= {lastname} />
            <p>{lnameError}</p>

            <input type={"email"} placeholder="E mail" className={styles["infoinput"]}  value={mail} onChange={e-mail}/>
            <p>{EmailError}</p>

            <input type={"number"} placeholder="+995 5_ _ _ _" className={styles["infoinput"]} />
            <p></p>
          </form>
          <div className={styles.pages}>
            <button className= {styles.nextPreviusPages}>{">"}</button>
            <div className={styles.eclipses}>
              <div className={styles.eclipseRed}></div>
              <div className={styles.eclipseLight}></div>
              <div className={styles.eclipseLight}></div>
              <div className={styles.eclipseLight}></div>
              <div className={styles.eclipseLight}></div>
            </div>
            <button  className= {styles.nextPreviusPages} onClick={submit}>{"<"}</button>
          </div>
        </div>
      </div>

      <div className={styles.cont2}>
        <h1>Redberry Origins</h1>
        <p>
          You watch ‚ÄúWhat? Where? When?‚Äù Yeah. Our founders used to play it.
          That‚Äôs where they got a question about a famous American author and
          screenwriter Ray Bradbury. Albeit, our CEO Gaga Darsalia forgot the
          exact name and he answered Ray Redberry. And at that moment, a name
          for a yet to be born company was inspired - Redberry üòá
        </p>
      </div>
    </div>
  );
}

export default PersonalInfo;
